import { Component, ViewChild, ElementRef, Renderer2, Input, Output, EventEmitter, OnChanges, SimpleChanges } from '@angular/core';
import { Pregunta } from '../interface/pregunta';

@Component({
  selector: 'app-preguntas',
  templateUrl: './preguntas.component.html',
  styleUrls: ['./preguntas.component.scss']
})
export class PreguntasComponent implements OnChanges{
  contador_pregunta : number = 0;
  @Input() pregunta!: Pregunta;
  @Output() botonAparece = new EventEmitter();
  @ViewChild('imagen') imagen!: ElementRef;
  @ViewChild('{{pregunta.nombre_img}}_si') radio_si!: ElementRef<HTMLInputElement>;
  @ViewChild('{{pregunta.nombre_img}}_no') radio_no!: ElementRef<HTMLInputElement>;

  constructor(private renderer2: Renderer2){}


  ngOnChanges(changes: SimpleChanges) {
    if(this.radio_si != null && this.radio_no != null){
      const imagen = this.imagen.nativeElement;
      console.log(imagen);
      const radio_si = this.radio_si.nativeElement;
      const radio_no = this.radio_no.nativeElement;
      if(radio_si.checked == true){
        radio_si.checked = false;
      }
      else{
        radio_no.checked = false;
      }
    }
  }

  dandoValor($event: any): void{
    const radio_si = this.radio_si.nativeElement;
    const radio_no = this.radio_no.nativeElement;
    this.renderer2.setAttribute(radio_si,"value","Si");
    this.renderer2.setAttribute(radio_no,"value","No");
    this.botonAparece.emit($event.target.value);

  }


}
<div class="row">
    <div class="col-12 col-md-12">
      <h3 >{{pregunta.enunciado}}</h3>
      <img *ngIf="pregunta.nombre_img" #imagen class="img-fluid w-25" src="../../assets/imagenes/apto_sangre/{{pregunta.nombre_img}}">
    </div>
    <div class="row">
      <div class="col-12">
        <input #{{pregunta.nombre_img}}_si type="radio" name="{{pregunta.nombre_pregunta}}" (change)="dandoValor($event)" class="ms-3">
        <label for="{{pregunta.nombre_pregunta}}_si">Si</label>
        <input #{{pregunta.nombre_img}}_no type="radio" name="{{pregunta.nombre_pregunta}}" (change)="dandoValor($event)" class="ms-3">
        <label for="{{pregunta.nombre_pregunta}}_no">No</label>
      </div>
    </div>
</div>
