import { Component, ViewChild, ElementRef } from '@angular/core';
import { trigger, style, transition, animate, state } from '@angular/animations'

export const tiempoAnimacion = 250;

@Component({
  selector: 'app-newsletter-ventana',
  templateUrl: './newsletter-ventana.component.html',
  styleUrls: ['./newsletter-ventana.component.scss'],
  animations: [
    trigger('entradaSalidaVentana', [
      transition('void => *', [
        style({
          opacity: '0',
          transform: 'translateY(20%)'
        }),
        animate(tiempoAnimacion,
          style({
            opacity: '1',
            transform: 'translateY(0%)'
          })
        ),
      ]),
      transition('* => void', [
        style({
          opacity: '1',
          transform: 'translateY(0%)'
        }),
        animate(tiempoAnimacion,
          style({
            opacity: '0',
            transform: 'translateY(20%)'
          })
        ),
      ]),
      transition('* => *', [
        style({
          opacity: '0',
          transform: 'translateY(20%)'
        }),
        animate(tiempoAnimacion,
          style({
            opacity: '1',
            transform: 'translateY(0%)'
          })
        ),
      ])
    ])
  ]
})
export class NewsletterVentanaComponent {

/*   @ViewChild('newsletter') newsletter!: ElementRef<HTMLDivElement>; */

  mostrar: boolean = true;
  suscrito: boolean = false;
  titulo: string = '¡No te pierdas nada!';
  texto: string = 'Suscríbete a nuestra newsletter para estar al tanto de noticias y eventos.';
  icono: string = 'fa-paper-plane';

  cerrar() {
    this.mostrar = false;
  }

  cambiarMensaje(event: boolean) {
    this.suscrito = true;

    this.titulo = '¡Gracias por apuntarte!';
    this.texto = 'Hemos enviado un enlace de verificación a tu correo ¡No olvides visitarlo para empezar a recibir notificaciones!';
    this.icono = 'fa-envelope-circle-check';

    setTimeout(() => this.cerrar(), 5000);
  }
}
<div #newsletter class="newsletter container" [@entradaSalidaVentana]="!suscrito" *ngIf="mostrar">

  <div class="row">

    <div class="col-2 icono-grande align-items-center d-flex">
      <i class="fa-solid {{ icono }} text-center"></i>
    </div>

    <div class="col-12 col-sm-10 text-start mensaje">

      <div class="d-flex justify-content-between pb-1">
        <i class="fa-solid {{ icono }} icono-peq"></i>
        <p class="titulo"> {{ titulo }} </p>
        <i class="fa-solid fa-xmark" (click)="cerrar()"></i>
      </div>

      <div class="pb-1">
        <p class="mb-2"> {{ texto }} </p>
      </div>

      <app-email-formulario (onSubmit)="cambiarMensaje($event)" *ngIf="!suscrito"></app-email-formulario>

    </div>
  </div>
</div>
