import { Component, ElementRef, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { NgbDatepickerModule, NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-modal',
  standalone: true,
	imports: [NgbDatepickerModule],
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.scss']
})
export class ModalComponent {
  closeResult = '';

  @ViewChild('content') content!: ElementRef;

  constructor(
    private modalService: NgbModal,
    private router: Router) {}


  ngAfterViewInit() {
  }

  // openModal(){
  //   this.modalService.open(this.content, { centered: true });
  // }

	open(content: any) {
		this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title', backdrop: 'static', keyboard: false }).result.then(
			(result) => {
				this.closeResult = `Closed with: ${result}`;
        console.log(this.closeResult);
			}
		);
	}

  irAlLogin() {
    this.router.navigate(['/auth/login']);
    this.modalService.dismissAll();
  }

  atras() {
    this.modalService.dismissAll();
  }

}
<ng-template #content let-modal>
	<div class="modal-header">
		<h4 class="modal-title titulo" id="modal-basic-title">Inicio de sesión necesario.</h4>
	</div>
	<div class="modal-body px-1 py-5 px-md-3 p-lg-5">
		Es necesario que inicies sesión para poder pedir cita.
	</div>
	<div class="modal-footer border-0">
		<button type="button" class="btn btn-primario" (click)="irAlLogin()">Iniciar sesión</button>
        <button type="button" class="btn btn-secundario" (click)="atras()">Atrás</button>
	</div>
</ng-template>


<hr/>

<pre>{{ closeResult }}</pre>
