import { Component, EventEmitter, Output } from '@angular/core';
import { FormControl, FormGroup, Validators, FormsModule } from '@angular/forms';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-recuperar-passwd',
  templateUrl: './recuperar-passwd.component.html',
  styleUrls: ['./recuperar-passwd.component.scss']
})
export class RecuperarPasswdComponent {

  emailRecPasswdEnviado: number = -1;

  constructor(
    private authHttsService: AuthService,
    private router: Router,
  ) {}

  ngOnInit() {

    this.emailRecPasswdEnviado = -1;
  }

  @Output() onRecPasswd: EventEmitter<number> = new EventEmitter();

  emailRecPasswdForm: FormGroup = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$")])
  });

  codForm: FormGroup = new FormGroup({
    cod: new FormControl('', [Validators.required, Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$")])
  });


  mandarEmailRecPasswd() {
    this.authHttsService.solicitarRecPasswd(this.emailRecPasswdForm.get('email')?.value).subscribe(resp => {
      if (resp.success) {

        localStorage.setItem('user', JSON.stringify({id: resp.id}));
        this.emailRecPasswdEnviado = 0;
        this.onRecPasswd.emit(2);
      }
      else {
        this.emailRecPasswdEnviado = 1;
        this.onRecPasswd.emit(3);
      }

    });
  }


  recuperarPasswd() {
    const user = JSON.parse(localStorage.getItem('user') || '');
    this.authHttsService.recuperarPasswd(user.id, this.codForm.get('cod')?.value).subscribe(resp => {
      if (resp.success) {

        this.onRecPasswd.emit(0);
      }
      else {
        this.onRecPasswd.emit(1);
      }
    });
  }

  irAlLogin() {
    this.router.navigate(['/auth/login']);
  }
}
<form *ngIf="this.emailRecPasswdEnviado == -1 || this.emailRecPasswdEnviado == 1"
[formGroup]="emailRecPasswdForm" (ngSubmit)="mandarEmailRecPasswd()">

<div class="mt-4 mb-2">
    <label class="form-label" for="emailRecPasswd">Email</label><br>
    <input
        type="text"
        class="form-control"
        name="email"
        placeholder="Introduce una dirección de email"
        formControlName="email"
    >
</div>

<span
    class="form-text errorLogin mb-2"
    *ngIf="this.emailRecPasswdForm.get('email')?.errors?.['pattern']
        && this.emailRecPasswdForm.get('email')?.touched"
>Formato de email inválido</span>
<span
    class="form-text errorLogin mb-3"
    *ngIf="this.emailRecPasswdForm.get('email')?.errors?.['required']
        && this.emailRecPasswdForm.get('email')?.touched"
>Este campo es obligatorio</span>

<div class="card-body w-100 p-0 mt-5 d-flex justify-content-center">
    <button
        type="submit"
        class="btn-primario mb-3"
        [disabled]="emailRecPasswdForm.invalid">Aceptar</button>
</div>

<div class="card-body w-100 p-0 mt-3 d-flex justify-content-center">
    <button
        type="button"
        class="btn-secundariod-block"
        (click)="irAlLogin()">Cancelar</button>
</div>

</form>



<form *ngIf="this.emailRecPasswdEnviado == 0"
[formGroup]="codForm" (ngSubmit)="recuperarPasswd()">

<div class="mt-4 mb-2">
    <label class="form-label" for="emailRecPasswd">Código</label><br>
    <input
        type="text"
        class="form-control"
        name="cod"
        placeholder="Introduce el código"
        formControlName="cod"
    >
</div>

<div class="card-body p-0 mt-5">
    <button
    type="submit"
    class="btn btn-primario mb-3"
    [disabled]="emailRecPasswdForm.invalid">Aceptar</button>
    <button
        type="button"
        class="btn btn-secundario"
        (click)="irAlLogin()">Ir al login</button>
</div>
</form>
