import { Component, EventEmitter, Output } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { SharedService } from '../../services/shared.service';
import { Email } from '../../interfaces/shared.interface';

@Component({
  selector: 'app-email-formulario',
  templateUrl: './email-formulario.component.html',
  styleUrls: ['./email-formulario.component.scss']
})
export class EmailFormularioComponent {

  @Output() onSubmit: EventEmitter<boolean> = new EventEmitter();

  emailForm!: FormGroup;
  mensaje: string = '';
  icono: string = '';
  suscrito: boolean | undefined;

  constructor(
    private fb: FormBuilder,
    private sharedService: SharedService
  ) { }

  ngOnInit(): void {
    this.emailForm = this.fb.group({
      email: ['', Validators.compose([
        Validators.required,
        Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$")
      ])]
    });
  }

  suscribirse() {
    if (this.emailForm.valid) {

      const email: Email = this.emailForm.value;

      this.sharedService.suscripcionNewsletter(email)
        .subscribe(resp => {
          this.mensaje = resp.msg;

          if (resp.success) {
            this.icono = 'fa-circle-check';
            this.onSubmit.emit(true);
            return;
          } else this.icono = 'fa-triangle-exclamation';
        });

    } else {
      this.mensaje = 'Email no válido';
      this.icono = 'fa-triangle-exclamation';
    }
  }
}
<form [formGroup]="emailForm" (ngSubmit)="suscribirse()" #form="ngForm">

  <div class="row gx-0">
    <input type="email"
      class="col-7"
      formControlName="email"
      placeholder="Introduce tu email"
      [class.error-input]="form.submitted && emailForm.get('email')?.invalid">

    <button type="submit" class="col-5 btn-primario">Suscribirse</button><br>
  </div>

  <div class="mensaje-error mt-2 gx-0">
    <i class="fa-solid {{ icono }} me-2"></i>
    <span>{{ mensaje }}</span>
  </div>

</form>
