import { Component } from '@angular/core';
import { UserLogin } from '../interfaces/auth.interface';
import { AuthService } from '../services/auth.service';
import { Md5 } from 'ts-md5';
import { Router } from '@angular/router';
import { SharedService } from 'src/app/shared/services/shared.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})

export class LoginComponent {
  erroneo: boolean = false;

  nuevoUsr: UserLogin = {
    email: '',
    passwd: ''
  };

  constructor(
    private authHttsService: AuthService,
    private SharedService: SharedService,
    private router: Router
  ) { }

  irARegistro() {
    this.router.navigate(['/auth/registro']);
  }

  login() {

    const passwd = Md5.hashStr(this.nuevoUsr.passwd);
    this.authHttsService.login({ email: this.nuevoUsr.email, passwd: passwd }).subscribe(resp => {

      if (resp.success) {
        localStorage.setItem('user', JSON.stringify(resp.data));
        this.erroneo = false;
        this.SharedService.comprobarPermisos.next(!this.erroneo);
        this.router.navigate(['']);
      }
      else this.erroneo = true;
    });
  }

  irARecPasswd() {
    this.router.navigate(['/auth/recuperacionpasswd']);
  }
}
<form (ngSubmit)="login()">
    <div *ngIf="this.erroneo" class="form-text errorLogin">
        Usuario o constraseña incorrectos
    </div>

    <div class="email mb-3">
        <label class="form-label" for="email">Email</label><br>
        <input
            type="text"
            class="form-control"
            name="email"
            placeholder="Introduce tu email"
            [(ngModel)]="nuevoUsr.email"
        >
    </div>

    <div class="passwd mt-4">
        <label class="form-label" for="passwd">Constraseña</label><br>
        <input
            type="password"
            class="form-control"
            name="passwd"
            placeholder="Introduce tu contraseña"
            [(ngModel)]="nuevoUsr.passwd"
        >
        <div class="mt-2 mb-5 d-flex justify-content-end">
            <a
                class="me-2 olvidoPasswd"
                (click)="irARecPasswd()">¿Olvidaste tu contraseña?</a>
        </div>
    </div>


    <div class="botonesLogin d-flex justify-content-center">
        <button type="submit" class="btn-primario">Aceptar</button>
    </div>

</form>

<div class="card-body w-100 p-0 d-flex justify-content-center mb-5">
    <div class="text-center mt-5 ">¿Aún no te has registrado?</div>
</div>

<div class="card-body w-100 p-0 d-flex justify-content-center">
        <button
        type="button"
        class="btn btn-secundario"
        (click)="irARegistro()"
        >Registrarse</button>
</div>
